    private void cmbhotelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmbhotelActionPerformed
    if (cmbhotel.getSelectedItem().equals("Pilih Hotel")) {
        bg2.clearSelection();
        txtharga.setText("");
    }   else if (cmbhotel.getSelectedItem().equals("Hotel Grand Mahkota")) {
       hekonomi  = 450000;
       hvip    = 1000000;
    } else if (cmbhotel.getSelectedItem().equals("Hotel California")) {
       hekonomi  = 600000;
       hvip    = 1200000;
    }
    }//GEN-LAST:event_cmbhotelActionPerformed

