    private void proseshotelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_proseshotelActionPerformed
        // TODO add your handling code here:
        String Namaa = namahotel.getText();
        String Hotel = (String) cmbhotel.getSelectedItem();
        String Durasi = (String) cmbdurasi.getSelectedItem();
        String Tamu = (String) cmbtamu.getSelectedItem();
        String Harga = txtharga.getText();
        String Total = txttotal.getText();
        bookinghotel bo = new bookinghotel(Namaa, Hotel, Durasi, Tamu, Harga, Total);
        bo.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_proseshotelActionPerformed

