    private void rekonomiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rekonomiActionPerformed
    if (rekonomi.isSelected()) {
        txtharga.setText(String.valueOf(hekonomi)); 
    }
    }//GEN-LAST:event_rekonomiActionPerformed

    private void resethotelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_resethotelActionPerformed
     cmbhotel.removeAllItems();
     cmbtamu.removeAllItems();
     cmbdurasi.removeAllItems();
     txtharga.setText("");
     txttotal.setText("");
    }//GEN-LAST:event_resethotelActionPerformed

    private void hitunghotelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitunghotelActionPerformed
        // TODO add your handling code here:
       String durasi = String.valueOf(cmbdurasi.getSelectedIndex());
        int lamasewa = Integer.parseInt(durasi);
        int hargakamar = Integer.parseInt(txtharga.getText());
        int totalbayar = lamasewa*hargakamar;
        txttotal.setText(""+totalbayar);
    }//GEN-LAST:event_hitunghotelActionPerformed

