    private void cmbtujuansawatActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmbtujuansawatActionPerformed
    if (cmbtujuansawat.getSelectedItem().equals("Pilih Tujuan")) {
        bg.clearSelection();
        txtharga.setText("");
    }   else if (cmbtujuansawat.getSelectedItem().equals("Pontianak - Jakarta")) {
       hdewasa  = 400000;
       hanak    = 200000;
    } else if (cmbtujuansawat.getSelectedItem().equals("Pontianak - Bandung")) {
       hdewasa  = 500000;
       hanak    = 250000;
    } else if (cmbtujuansawat.getSelectedItem().equals("Jakarta - Jayapura")) {
        hdewasa = 600000;
        hanak   = 300000;
    }
    }//GEN-LAST:event_cmbtujuansawatActionPerformed

