    private void prosesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_prosesActionPerformed
        String Namaa = nama.getText();
        String Maskapai = (String) cmbsawat.getSelectedItem();
        String Tujuan = (String) cmbtujuansawat.getSelectedItem();
        String Jumlah = (String) cmborang.getSelectedItem();
        String Harga = txtharga.getText();
        String Total = txttotal.getText();
        tiketsawat t = new tiketsawat(Namaa, Maskapai, Tujuan, Jumlah, Harga, Total);
        t.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_prosesActionPerformed

