    private void rdanakActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rdanakActionPerformed
    if (rdanak.isSelected()) {
        txtharga.setText(String.valueOf(hanak));
    }
    }//GEN-LAST:event_rdanakActionPerformed

    private void rddewasaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rddewasaActionPerformed
    if (rddewasa.isSelected()) {
        txtharga.setText(String.valueOf(hdewasa));
    }
    }//GEN-LAST:event_rddewasaActionPerformed

